pragma solidity ^0.5.8;

import './math/SafeMath.sol';
import './token/ERC20.sol';
import './ownership/Ownable.sol';

/**
 * @title The MDTDataReward contract
 * @dev Share data and receive MDT reward
 * @author MDT Team
 */
contract MDTDataReward is Ownable {
    using SafeMath for uint256;

    // Data reward record
    struct Reward {
        uint256 claimableValue;     // amount of tokens that are claimable
        uint256 totalTokensClaimed; // total tokens claimed
        uint256 lastClaimedTime;    // latest claimed time
        bool initialized;           // whether the reward is initialized
        bool enabled;               // whether the reward is enabled
    }

    ERC20 public token;                 // MDT contract address
    uint256 public totalTokensClaimed;  // total tokens claimed by users
    bool public isSuspended;            // whether to suspend claiming tokens

    mapping (address => Reward) public dataRewards; // all data rewards records
    address[] public boundAddresses;                // addresses that have data rewards

    // Events
    event TokensWithdrawn(address indexed _to, uint256 _amount);
    event RewardDelivered(address indexed _to, uint256 _amount);
    event RewardClaimed(address indexed _to, uint256 _amount, uint256 _claimedTime);
    event RewardCanceled(address indexed _to, uint256 _amount);
    event RewardTransferred(address indexed _from, address indexed _to, uint256 _amount);

    /// @dev Reverts if address is 0x0 or this token address
    modifier validRecipient(address _recipient) {
        require(validAddress(_recipient), "recipient cannot be zero address and the contract address");
        _;
    }

    /**
    * @dev MDTDataReward contract constructor.
    * @param _tokenAddress address The MDT contract address.
    */
    constructor(address _tokenAddress) public {
        require(_tokenAddress != address(0), "token address cannot be zero");

        token = ERC20(_tokenAddress);
    }

    /**
     * @dev Set data reward to enabled or disabled for a wallet address. (contract owner only)
     * @param _address address Wallet address.
     * @param _isEnabled true to set reward to be enabled.
     */
    function setRewardEnabled(address _address, bool _isEnabled)
        public
        onlyOwner
        validRecipient(_address)
    {
        Reward storage reward = dataRewards[_address];
        if (reward.enabled && !_isEnabled) {
            reward.enabled = false;
        } else if (!reward.enabled && _isEnabled) {
            if (!reward.initialized) {
                boundAddresses.push(_address);
                reward.initialized = true;
            }
            reward.enabled = true;
        }
    }

    /**
     * @dev Set data rewards to enabled or disabled for a list of wallet addresses. (contract owner only)
     * @param _addresses address[] List of wallet addresses.
     * @param _isEnabled true to set rewards to be enabled.
     */
    function setRewardsEnabled(address[] memory _addresses, bool _isEnabled) public onlyOwner {
        for (uint i = 0; i < _addresses.length; i++) {
            setRewardEnabled(_addresses[i], _isEnabled);
        }
    }

    /**
     * @dev Deliver data rewards to users. (contract owner only)
     * @param _addresses address[] Addresses that MDT users binded to receive data rewards.
     * @param _values uint256[] Amount of tokens earned.
     */
    function deliverRewards(address[] memory _addresses, uint256[] memory _values) public onlyOwner {
        require(_addresses.length == _values.length, "length of addresses array must be equal to values array");

        for (uint i = 0; i < _addresses.length; i++) {
            require(validAddress(_addresses[i]), "address cannot be zero or this contract address");
            require(_values[i] > 0, "value must be greater than zero");
            Reward storage reward = dataRewards[_addresses[i]];
            reward.claimableValue = reward.claimableValue.add(_values[i]);
            if (!reward.initialized) { // set reward to be initialized and enabled if it is not initialized
                boundAddresses.push(_addresses[i]);
                reward.initialized = true;
                reward.enabled = true;
            } // if the reward was initialized, the enabled status remains
            emit RewardDelivered(_addresses[i], _values[i]);
        }
    }

    /**
     * @dev Cancel expired data rewards if users don't claim within a certain period. (contract owner only)
     * @param _addresses address[] Addressses that have expired data rewards.
     * @param _values uint256[] Amount of tokens to cancel from an address.
     */
    function cancelRewards(address[] memory _addresses, uint256[] memory _values) public onlyOwner {
        require(_addresses.length == _values.length, "length of addresses array must be equal to values array");

        for (uint i = 0; i < _addresses.length; i++) {
            require(_values[i] > 0, "value must be greater than zero");
            Reward storage reward = dataRewards[_addresses[i]];
            require(reward.claimableValue > 0 && _values[i] <= reward.claimableValue,  // if the address is invalid, this check will fail
                "amount of tokens to be canceled should be smaller than or equal to the claimable value");
            reward.claimableValue = reward.claimableValue.sub(_values[i]);
            emit RewardCanceled(_addresses[i], _values[i]);
        }
    }

    /**
     * @dev Transfer tokens between wallets, used when user binds the account to a different wallet address. (contract owner only)
     * @param _fromAddress address The wallet address to transfer tokens from
     * @param _toAddress address The wwallet address to transfer tokens to
     * @param _amount amount of tokens to be transfered
     */
    function transferReward(address _fromAddress, address _toAddress, uint256 _amount)
        public
        onlyOwner
        validRecipient(_fromAddress)
        validRecipient(_toAddress)
    {
        require(_amount > 0 && _fromAddress != _toAddress, "amount must be greater than zero and fromAddress must not equal to toAddress");
        Reward storage fromReward = dataRewards[_fromAddress];
        Reward storage toReward = dataRewards[_toAddress];
        require(fromReward.claimableValue > 0 && _amount <= fromReward.claimableValue, "invalid amount to transfer from an address");
        fromReward.claimableValue = fromReward.claimableValue.sub(_amount);
        toReward.claimableValue = toReward.claimableValue.add(_amount);
        if (!toReward.initialized) { // set toReward to be initialized and enabled if it is not initialized
            boundAddresses.push(_toAddress);
            toReward.initialized = true;
            toReward.enabled = true;
        }

        emit RewardTransferred(_fromAddress, _toAddress, _amount);
    }

    /**
     * @dev claim reward earned.
     * @return amount of tokens claimed.
     */
    function claimReward() public returns (uint256) {
        require(canClaimReward(), "reward is not found or unable to claim now");
        Reward storage reward = dataRewards[msg.sender];
        uint256 value = reward.claimableValue;
        reward.claimableValue = 0;
        reward.totalTokensClaimed = reward.totalTokensClaimed.add(value);
        reward.lastClaimedTime = now;
        totalTokensClaimed = totalTokensClaimed.add(value);
        require(token.transfer(msg.sender, value) == true, "failed to transfer tokens");
        emit RewardClaimed(msg.sender, value, reward.lastClaimedTime);
        return value;
    }

    /**
     * @dev Withdraw tokens from the contract. (contract owner only)
     * @param _tokenReceiver address Wallet for receiving tokens.
     * @param _amount uint256 Amount of tokens.
     */
    function withdrawTokens(address _tokenReceiver, uint256 _amount)
        public
        onlyOwner
        validRecipient(_tokenReceiver)
    {
        require(_amount > 0, "amount must be greater than zero");
        require(token.transfer(_tokenReceiver, _amount) == true, "failed to transfer tokens");
        emit TokensWithdrawn(_tokenReceiver, _amount);
    }

    /**
     * @dev Suspend the data reward contract. (contract owner only)
     */
    function suspend() public onlyOwner {
        if (isSuspended) {
            return;
        }
        isSuspended = true;
    }

    /**
     * @dev Resume the data reward contract. (contract owner only)
     */
    function resume() public onlyOwner {
        if (!isSuspended) {
            return;
        }
        isSuspended = false;
    }

    /**
     * @dev Transfer to owner any tokens send by mistake to this contract. (contract owner only)
     * @param _token ERC20 The address of the token to transfer.
     * @param amount uint256 The amount to be transferred.
     */
    function emergencyERC20Drain(ERC20 _token, uint256 amount) public onlyOwner {
        _token.transfer(owner, amount);
    }

    /**
     * @dev Get token balance of a wallet address.
     * @param _address address Address to be queried.
     * @return the token balance of a wallet address.
     */
    function tokenBalanceOf(address _address) public view returns (uint256) {
        return token.balanceOf(_address);
    }

    /**
     * @dev Get token balance of this contract.
     * @return the token balance of this contract.
     */
    function contractTokenBalance() public view returns (uint256) {
        return token.balanceOf(address(this));
    }

    /**
     * @dev Get total tokens claimed for the caller address.
     * @return amount of tokens claimed.
     */
    function getTotalTokensClaimed() public view returns (uint256) {
        return dataRewards[msg.sender].totalTokensClaimed;
    }

    /**
     * @dev Get amount of claimable tokens for the caller address.
     * @return amount of claimable tokens.
     */
    function getClaimableValue() public view returns (uint256) {
        return dataRewards[msg.sender].claimableValue;
    }

    /**
     * @dev Get bound addresses.
     * @return bound addresses.
     */
    function getBoundAddresses() public view returns (address[] memory) {
        return boundAddresses;
    }

    /**
     * @dev Check if the caller address is bound to the contract. (ever setup via deliverRewards or setRewardEnabled)
     * @return true if the caller address is bound.
     */
    function isAddressBound() public view returns (bool) {
        return dataRewards[msg.sender].initialized;
    }

    /**
     * @dev Check if the caller address has claimable reward.
     * @return true if the caller has claimable reward.
     */
    function canClaimReward() public view returns (bool) {
        if (isSuspended) {
            return false;
        }
        Reward storage reward = dataRewards[msg.sender];
        return reward.enabled && reward.claimableValue > 0;
    }

    /**
     * @dev Get reward record for the caller.
     * @return a tuple of reward record (claimableValue, totalTokensClaimed, lastClaimedTime, initialized, enabled).
     */
    function getRewardRecord() public view returns (uint256, uint256, uint256, bool, bool) {
        Reward storage reward = dataRewards[msg.sender];
        return (reward.claimableValue, reward.totalTokensClaimed, reward.lastClaimedTime, reward.initialized, reward.enabled);
    }

    /**
     * @dev Get reward record for address. (contract owner only)
     * @param _address address Wallet address.
     * @return a tuple of reward record (claimableValue, totalTokensClaimed, lastClaimedTime, initialized, enabled).
     */
    function getRewardRecordForAddress(address _address) public onlyOwner view returns (uint256, uint256, uint256, bool, bool) {
        Reward storage reward = dataRewards[_address];
        return (reward.claimableValue, reward.totalTokensClaimed, reward.lastClaimedTime, reward.initialized, reward.enabled);
    }

    /**
     * @dev Check if address is valid.
     * @param _address address Wallet address.
     * @return true if the address is valid.
     */
    function validAddress(address _address) private view returns (bool) {
        return _address != address(0) && _address != address(this);
    }
}

